const UseStateCounterExample = () => {
  return (
    <>
      <h3> Counter </h3>
      <button type="button" className="btn">
        Increase
      </button>
      <button type="button" className="btn">
        Reset
      </button>
      <button type="button" className="btn">
        Decrease
      </button>
    </>
  );
};

export default UseStateCounterExample;
