import React, { useState, useEffect } from "react";

//UseEffect is for working on Sideeffects
//Usually called after render/re-render
//Usestate will make the component to rerender and when re-rendering the useEffect gets called
const UseEffectBasicExample = () => {
  let [value, setValue] = useState(0);
  useEffect(() => {
    console.log("Calling useEffect");
    document.title = `New value (${value})`;
  });
  const handleClick = () => {
    setValue(value + 1);
  };
  return (
    <>
      <h3>{value}</h3>
      <button className="btn" type="button" onClick={handleClick}>
        Click Me
      </button>
    </>
  );
};

export default UseEffectBasicExample;
