import React, { useState } from "react";

//Refactored Controlled inputs code here
const MultipleInput = () => {
  const [person, setPerson] = useState({ firstName: "", age: "", email: "" });
  const [people, setPeople] = useState([]);
  const handleOnChange = (e) => {
    nameVal = e.target.name;
    value = e.target.value;
    setPerson({ ...person, [nameVal]: value });
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    if (person.firstName && person.age && person.email) {
      const newPerson = { id: Date.now(), ...person };
      setPeople([...people, newPerson]);
      setPerson({ firstName: "", age: "", email: "" });
    }
  };
  return (
    <>
      <div>
        <form className="form">
          <div className="form-control">
            <label htmlFor="firstName">FirstName:</label>
            <input
              type="text"
              id="firstName"
              value={person.firstName}
              onChange={handleOnChange}
            />
          </div>
          <div className="form-control">
            <label htmlFor="email">Email:</label>
            <input
              type="text"
              id="email"
              value={person.email}
              onChange={handleOnChange}
            />
          </div>
          <div className="form-control">
            <label htmlFor="age">Age:</label>
            <input
              type="text"
              id="age"
              value={person.age}
              onChange={handleOnChange}
            />
          </div>
          <button type="submit" onSubmit={handleSubmit}>
            Add Person
          </button>
        </form>
        {people.map((person) => {
          const { id, firstName, email } = person;
          return (
            <div className="item" key={id}>
              <h4>{firstName}</h4>
              <p>{email}</p>
            </div>
          );
        })}
      </div>
    </>
  );
};

export default MultipleInput;
